  return json
}  // async await 방법*/

const BASE_URL = `https://api.coinpaprika.com/v1`;

export function fetchCoins () { // promise 방법
  return fetch(`${BASE_URL}/coins`).then((response) => response.json());
}

export function fetchCoinInfo (coinId:string) { // info data fetcher
  return fetch(`${BASE_URL}/coins/${coinId}`).then((response) => response.json());
}

export function fetchCoinTickers (coinId:string) { // price data fetcher
  return fetch(`${BASE_URL}/tickers/${coinId}`).then((response) => response.json());
}

export async function fetchCoinHistory (coinId:string) { // cahrt 컴포넌트에 사용될 fetcher
  const endDate = Math.floor(Date.now() / 1000); // 지금 시점
  const startDate = endDate - 60*60*24*7; // 지금으로부터 일주일 전
/*   return fetch(`${BASE_URL}/tickers/${coinId}/ohlc/historical?start=${startDate}&end=${endDate}`).then((response) => response.json()); 
   */
const response = await fetch(`${BASE_URL}/tickers/${coinId}/ohlc/historical?start=${startDate}&end=${endDate}`);
const json = await response.json();
return json
}
