import { useState } from "react";
import {useParams} from "react-router"
import { useLocation } from "react-router-dom";
import styled from "styled-components";

const Container = styled.div`
  padding: 0px 10px;
  max-width: 480px;
  margin: 0 auto;
`;
const Header = styled.header`
  height: 10vp;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const Loader = styled.span`
  text-align: center;
  display: block;
`;
const Title = styled.h1`
  margin: 20px;
  color: ${(props) => props.theme.accentColor};
  font-size: 45px;
`

interface RouteState {
  state: {
  name: string;
  rank: number;
}
};

function Coin () {
  const {coinId} = useParams(); // <Route>의 path에 적용되는 parameter를 호출한다. 강의와 달리 react-router-dom v6에서는 useParams() 호출 시 기본값으로 type이 string or undefined로 지정되므로 interface 또는 type 지정 불필요. {}로 parameter를 열어준다.
  const [loading, setLoading] = useState(true);
  const location = useLocation(); // useLocation() => location 관련 객체 전달. 여기서 <Link>안의 state에 입력한 정보 확인 가능.
  const {state} = location.state as RouteState;
  console.log(state);
  return <Container>
  <Header>
    <Title>{state}</Title>
  </Header>
  {loading ? <Loader>Loading...</Loader> : null}
  </Container>;
} 

export default Coin
